package nikita123;

public class EvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int temp=0;
		for(int i=0;i<=10;i++)
			
		{
		
			temp=i;
			
			if(temp%2==0)
				
			{
				System.out.println("Number is even"+temp);
			}
				
			else {
				
				System.out.println("Number is odd"+temp);
			}
			
		} 	
		
		
	}

	
	
}
